func (kgb *KpiGroupBy) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range kgb.fields {
		if !kpi.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for group-by", f)}
		}
	}
	selector := kgb.sqlQuery()
	if err := selector.Err(); err != nil {
		return err
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := kgb.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (kgb *KpiGroupBy) sqlQuery() *sql.Selector {
	selector := kgb.sql
	columns := make([]string, 0, len(kgb.fields)+len(kgb.fns))
	columns = append(columns, kgb.fields...)
	for _, fn := range kgb.fns {
		columns = append(columns, fn(selector, kpi.ValidColumn))
	}
	return selector.Select(columns...).GroupBy(kgb.fields...)
}

// KpiSelect is the builder for select fields of Kpi entities.
type KpiSelect struct {
	config
	fields []string
	// intermediate query (i.e. traversal path).
	sql  *sql.Selector
	path func(context.Context) (*sql.Selector, error)
}

// Scan applies the selector query and scan the result into the given value.
func (ks *KpiSelect) Scan(ctx context.Context, v interface{}) error {
	query, err := ks.path(ctx)
	if err != nil {
		return err
	}
	ks.sql = query
	return ks.sqlScan(ctx, v)
}

// ScanX is like Scan, but panics if an error occurs.
func (ks *KpiSelect) ScanX(ctx context.Context, v interface{}) {
	if err := ks.Scan(ctx, v); err != nil {
		panic(err)
	}
}

