func (ks *KpiSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range ks.fields {
		if !kpi.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := ks.sqlQuery().Query()
	if err := ks.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (ks *KpiSelect) sqlQuery() sql.Querier {
	selector := ks.sql
	selector.Select(selector.Columns(ks.fields...)...)
	return selector
}
