func (kcs *KpiCategorySelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range kcs.fields {
		if !kpicategory.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := kcs.sqlQuery().Query()
	if err := kcs.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (kcs *KpiCategorySelect) sqlQuery() sql.Querier {
	selector := kcs.sql
	selector.Select(selector.Columns(kcs.fields...)...)
	return selector
}
